mapper.select:
SELECT COUNT(*)
FROM LIKE_RECORD
WHERE target_type = #{targetType} AND target_id = #{targetId} AND mem_id = #{memId}

mapper.update:
UPDATE post
SET post_like_cnt = post_like_cnt + 1
WHERE post_id = #{postId}

UPDATE post
SET post_like_cnt = post_like_cnt - 1
WHERE post_id = #{postId}
